//解析数据
function live_print ($result) {
	$data=json_decode($result,true);
	if(!empty($data["content"]["liveList"])){
	echo '<tr><td colspan="8"><span style="color:Red">----------分界线，以下为直播----------</span></td></tr>';
		foreach ($data["content"]["liveList"] as $id => $content) {
		echo '<tr>';
		tablelist($content);
		echo '</tr>';
	}
	}
	if(!empty($data["content"]["reviewList"])){
	echo '<tr><td colspan="8"><span style="color:Red">----------录播分界线，以下为录播----------</span></td></tr>';
	foreach ($data["content"]["reviewList"] as $id => $content) {
		echo '<tr>';
		tablelist($content);
		echo '</tr>';
	}
	}
}

function openlive_print ($result,$f) {
	$data=json_decode($result,true);
	if(!empty($data["content"]["liveList"])) {
	echo '<tr><td colspan="8"><span style="color:Red">----------分界线，以下为公演----------</span></td></tr>';	
	foreach ($data["content"]["liveList"] as $id => $content) {
		$info=openlive_get_info($content,$f);
		
		//$info=json_decode($info,true);
		echo '<tr>';
		tablelist_open($info);
		echo '</tr>';
	}
	}
}
//辅助函数 将公演直播、录播数据提取的内容打印成一列
function tablelist_open($content) {
	global $order_open;
	foreach ($order_open as $key) {
		$value=$content[$key];
		switch ($key) {
			case "streamPathHd": //07超清源
				echo '<td class="t7"><a href="'.$value.'"  target="_blank">'.$value.'</a></td>';
				break;
			case "title": //02标题
				echo '<td class="t2">'.$value.'</td>';
				break;
			case "subTitle": //03副标题
				echo '<td class="t3">'.$value.'</td>';
				break;
			case "picPath": //06配图地址
				echo '<td class="t6">';
				$pics = explode(',',$value);
				foreach ($pics as $values) {
				echo '<img src="'.LIVE_PIC.$values.'" style="max-width:30px; max-height:30px" />';
				};
				echo '</td>';
				break;
			case "startTime": //05直播开始时间
				echo '<td class="t5">'.date("Y-m-d",substr($value,0,10)).'<br />'.date("H:i:s",substr($value,0,10)).'</td>';
				break;
			case "isReview": //04直播类型
				echo '<td class="t4">';
				if ($value==1) {
					echo '录播';
				}
				else if ($value==0) {
					echo '直播';
				}
				else if ($value==2) {
					echo '正在直播';
				} else {
					echo 'U_'.$value;
				}
				echo '</td>';
				break;
			case "streamPath": //09标清源
				echo '<td class="t9"><a href="'.$value.'"  target="_blank">'.$value.'</a></td>';
				break;
			case "streamPathLd": //08高清源
				echo '<td class="t8"><a href="'.$value.'"  target="_blank">'.$value.'</a></td>';
				break;
		}
	}
}

//辅助函数 将直播、录播数据提取的内容打印成一列
function tablelist($content) {
	global $order;
	foreach ($order as $key) {
		$value=$content[$key];
		switch ($key) {
			case "liveId": //07官方地址
				echo '<td class="t7"><a href="'.LIVE_SHARE.$value.'"  target="_blank">'.LIVE_SHARE.$value.'</a></td>';
				break;
			case "title": //02标题
				echo '<td class="t2">'.$value.'</td>';
				break;
			case "subTitle": //03副标题
				echo '<td class="t3">'.$value.'</td>';
				break;
			case "picPath": //06配图地址
				echo '<td class="t6">';
				$pics = explode(',',$value);
				foreach ($pics as $values) {
				echo '<img src="'.LIVE_PIC.$values.'" style="max-width:30px; max-height:30px" />';
				};
				echo '</td>';
				break;
			case "startTime": //05直播开始时间
				echo '<td class="t5">'.date("Y-m-d",substr($value,0,10)).'<br />'.date("H:i:s",substr($value,0,10)).'</td>';
				break;
			case "memberId": //01成员ID
				//echo '<td class="t1">'.$value.'</td>';
				break;
			case "liveType": //04直播类型
				echo '<td class="t4">';
				if ($value=="1") {
					echo '视频';
				}
				else if ($value=="2") {
					echo '电台';
				}
				else {
					echo 'U_'.$value;
				}
				echo '</td>';
				break;
			case "picLoopTime": //10图片循环时间
				//echo '<td class="t10">'.$value.'</td>';
				break;
			case "lrcPath": //09弹幕文件地址
				echo '<td class="t9"><a href="'.LIVE_PIC.$value.'"  target="_blank">'.LIVE_PIC.$value.'</a></td>';
				break;
			case "streamPath": //08视频源地址
				echo '<td class="t8"><a href="'.$value.'"  target="_blank">'.$value.'</a></td>';
				break;
			case "screenMode": //??
				//echo '<td class="t11">'.$value.'</td>';
				break;
		}
	}
}

