function teamid2name ($teamid) {
	global $teaminfo;
	if(teaminfo[$teamid]!='') {
		return $teaminfo[$teamid];
	}
}
function memberid2name ($memberid) {
	global $memberinfo;
	if(teaminfo[$memberid]!='') {
